import React from 'react';
import {connect} from 'react-redux';
import {
  getTotalBasketCount,
  getTotalBasketPrice
} from 'selectors';

import {Link} from 'react-router';

const BasketCart = ({totalBasketCount, totalBasketPrice}) => {
  return (
    <div className='cart cart-top navbar-right'>
      <div className='dropdown'>
        <Link 
          to='/basket' 
          id='dLabel' 
          className='btn btn-inverse btn-block btn-lg basket-span'
        >
          <span><i className='glyphicon glyphicon-shopping-cart' /> {totalBasketCount} товаров </span>
          <span>| Cумма: ${totalBasketPrice}</span>
        </Link>
      </div>
    </div>
  )
}

const mapDispatchToProps = (state) => ({
  totalBasketCount: getTotalBasketCount(state),
  totalBasketPrice: getTotalBasketPrice(state),
})

export default connect(mapDispatchToProps, null)(BasketCart)