import React from 'react';
import {connect} from 'react-redux';
import R from 'ramda';
import {Link} from 'react-router';
import TopNavigation from 'components/topNavigation';

import {
  getTotalBasketPrice,
  getBasketPhonesWithCount
} from 'selectors';
import {
  removePhoneFromBasket,
  basketCheckout,
  cleanBasket
} from 'actions';

const Basket = ({
  phones, 
  totalPrice, 
  removePhoneFromBasket,
  basketCheckout,
  cleanBasket,
  showPreload
}) => {
  const isBasketEmpty = R.isEmpty(phones)

  const renderContent = () => (
    <div>
      {isBasketEmpty && <div>Ваша корзина пуста</div>}

      <div className='table-responsive'>
        <table className='table-bordered table-striped table-condensed cf'>
          <tbody>
            {phones.map((phone, index) => (
              <tr
                key={index}
                className='item-checout'
              >
                <td className='first-column-checkout'>
                  <img 
                    className='img-thumbnail'
                    src={phone.image}
                    alt={phone.name}
                  />
                </td>
                <td>{phone.name}</td>
                <td>${phone.price}</td>
                <td>{phone.count}</td>
                <td>
                  <span 
                    className='delete-cart'
                    onClick={() => removePhoneFromBasket(phone.id)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {
        R.not(isBasketEmpty) && 
        <div className='row'>
          <div className='pull-right total-user-checkout'>
            <b>Общая сумма:</b>
            ${totalPrice}
          </div>
        </div>
      }
    </div>
  )
  
  let validTel = 0;
  const handleTel = (e) => {
      validTel = e.target.value
  }

  const buyItem = () => {
    basketCheckout(phones, validTel)
    validTel = 0;
  }

  const renderSidebar = () => (
    <div>
      <Link
        to='/'
        className='btn btn-info'
      >
        <span className='glyphicon glyphicon-info-sign'></span>
        <span> Продолжить покупки</span>
      </Link>
      {
        R.not(isBasketEmpty) &&
        <div>
          <button
            onClick={cleanBasket}
            className='btn btn-danger'
          >
            <span className='glyphicon glyphicon-trash' />
            <span> Очистить корзину</span>
          </button>
          <span>Введите номер телефона формата 0681112233</span>
          <input 
            onChange={handleTel}
            className='number-telephone'
            placeholder='Номер телефона...'
          />
          {showPreload === 'numberFailure' && <span>некорректный номер телефона</span>}
          <button 
            className='btn btn-success'
            onClick={buyItem}
            disabled={showPreload === 'true'}
          >
            <span className='glyphicon glyphicon-envelope' />
            <span> Оформить покупку</span>
          </button>
          {showPreload === 'true' && <div className='preload-mail'>отправка заказа...</div>}
        </div>
      }
    </div>
  )
  return (
    <div className='view-container'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-12'>
            <TopNavigation
              location=''
            />
          </div>
        </div>
        <div className='row'>
          <div className='col-md-9'>
            {renderContent()}
          </div>
          <div className='col-md-3 btn-user-checkout'>
            {showPreload === 'success' && <div>Спасибо что приобрели у нас товар, ближайшее время мы с Вами свяжемся!</div>}
            {renderSidebar()}
          </div>
        </div>
      </div>
    </div>
  )
}

const mapStateToProps = state => ({
  phones: getBasketPhonesWithCount(state),
  totalPrice: getTotalBasketPrice(state),
  showPreload: state.sendMail
})

const mapDispatchToProps = {
  removePhoneFromBasket,
  basketCheckout,
  cleanBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)